const TCS34725 = require('tcs34725');

// Initialize the sensor
TCS34725.init((err) => {
    if (err) {
        console.log(err);
        return;
    }
    console.log('Sensor initialized');
    
    // Read the color values every second
    setInterval(() => {
        TCS34725.getRGBC((err, data) => {
            if (err) {
                console.log(err);
                return;
            }
            
            console.log(`Red: ${data.r}, Green: ${data.g}, Blue: ${data.b}`);
        });
    }, 1000);
});
